import { Component, OnInit } from "@angular/core";
import { OrderDTO } from "src/app/dtos/order/order.dto";
import { environment } from "src/app/environments/environment";
import { Product } from "src/app/models/product";
import { CartService } from "src/app/service/cart.service";
import { OrderService } from "src/app/service/order.service";
import { ProductService } from "src/app/service/product.service";

@Component({
  selector: "app-order",
  templateUrl: "./order.component.html",
  styleUrls: ["./order.component.scss"],
})
export class OrderComponent implements OnInit {
  cartItems: { product: Product; quantity: number }[] = [];
  couponCode: string = "";
  totalAmount: number = 0;
  orderData: OrderDTO = {
    userId: 1,
    // fullname: '',  Khởi tạo rỗng, sẽ được điền từ form
    email: "",
    phone_number: "",
    address: "",
    note: "",
    total_money: 0,
    payment_method: "cod",
    shipping_method: "express",
    coupon_code: "",
    cart_items: [],
    fullname: "",
  };
  constructor(
    private cartService: CartService,
    private productService: ProductService,
    private orderService: OrderService
  ) {}

  ngOnInit(): void {
    debugger;
    // Lấy danh sách sản phẩm từ giỏ hàng
    const cart = this.cartService.getCart();
    // Chuyển danh sách ID từ Map giỏ hàng
    const productIds = Array.from(cart.keys());
    debugger;
    //get info detail product by list productIds
    this.productService.getProductsByIds(productIds).subscribe({
      next: (products) => {
        debugger;
        this.cartItems = productIds.map((productId) => {
          debugger;
          const product = products.find((p) => p.id === productId);
          if (product) {
            product.thumbnail = `${environment.apiBaseUrl}/products/images/${product.thumbnail}`;
          }
          return {
            product: product!,
            quantity: cart.get(productId)!,
          };
        });
        console.log("done");
      },
      complete: () => {
        debugger;
        this.calculateTotal();
      },
      error: (error: any) => {
        debugger;
        console.error("Error fetching detail: ", error);
      },
    });
  }

  placeOrder() {
    this.orderService.placeOrder(this.orderData).subscribe({
      next: (response) => {
        debugger;
        console.log("Đặt hàng thành công");
      },
      complete: () => {
        debugger;
        this.calculateTotal();
      },
      error: (error: any) => {
        debugger;
        console.error("Lỗi khi đặt hàng: ", error);
      },
    });
  }

  calculateTotal(): void {
    this.totalAmount = this.cartItems.reduce(
      (total, item) => total + item.product.price * item.quantity,
      0
    );
  }

  // Hàm xử lý việc áp dụng mã giảm giá
  applyCoupon(): void {
    // Viết mã xử lý áp dụng mã giảm giá ở đây
    // Cập nhật giá trị totalAmount dựa trên mã giảm giá nếu áp dụng
  }
}
<app-header></app-header>
<div class="container">
  <div class="intro-section">
    <h1>Đây là trang order</h1>
    <p>Sử dụng Bootstrap</p>
  </div>
  <div class="row">
    <div class="col-md-6">
      <h2 class="product-header">Thông tin người nhận</h2>
      <!-- <form> -->
      <div class="mb-3">
        <label for="fullname" class="form-label">Họ và tên</label>
        <input
          type="text"
          class="form-control"
          id="fullname"
          [(ngModel)]="orderData.fullname"
        />
      </div>
      <div class="mb-3">
        <label for="email" class="form-label">Email</label>
        <input
          type="email"
          class="form-control"
          id="email"
          placeholder="Email"
          [(ngModel)]="orderData.email"
        />
      </div>
      <div class="mb-3">
        <label for="phone" class="form-label">Số điện thoại</label>
        <input
          type="text"
          class="form-control"
          id="phone"
          [(ngModel)]="orderData.phone_number"
        />
      </div>
      <div class="mb-3">
        <label for="address" class="form-label">Địa chỉ</label>
        <input
          type="text"
          class="form-control"
          id="address"
          [(ngModel)]="orderData.address"
        />
      </div>
      <div class="mb-3">
        <label for="note" class="form-label">Ghi chú</label>
        <input
          type="text"
          class="form-control"
          id="note"
          [(ngModel)]="orderData.note"
        />
      </div>
      <div class="form-group">
        <label for="shippingMethod">Phương thức vận chuyển</label>
        <select
          class="form-control"
          id="shippingMethod"
          [(ngModel)]="orderData.shipping_method"
        >
          <option value="express">Nhanh (Express)</option>
          <option value="normal">Thường (Normal)</option>
        </select>
      </div>
      <div class="form-group">
        <label for="paymentMethod">Phương thức thanh toán</label>
        <select
          class="form-control"
          id="paymentMethod"
          [(ngModel)]="orderData.payment_method"
        >
          <option value="cod">Thanh toán khi nhận hàng (COD)</option>
          <option value="other">Thanh toán khác</option>
        </select>
      </div>
      <!-- </form> -->
    </div>

    <div class="col-md-6">
      <h2 class="product-order">Sản phẩm đã đặt hàng</h2>
      <table>
        <thead>
          <tr>
            <th scope="col" class="text-start">Sản phẩm</th>
            <th scope="col">Số lượng</th>
            <th scope="col">Đơn giá</th>
            <th scope="col">Tổng giá</th>
          </tr>
        </thead>
        <tbody>
          <ng-container *ngFor="let item of cartItems">
            <tr>
              <td>
                <div class="product-info">
                  <img
                    [src]="item.product.thumbnail"
                    alt="Product Image"
                    class="product-image"
                  />
                  <span class="product-name">{{ item.product.name }}</span>
                </div>
              </td>
              <td>{{ item.quantity }}</td>
              <td>{{ item.product.price }}</td>
              <td>
                {{ item.product.price * item.quantity | number : "1.2-2" }}
              </td>
            </tr>
          </ng-container>
        </tbody>
      </table>
      <div class="text-start mt-3">
        <h4 class="header-text text-end">Tổng giá: {{ totalAmount }}</h4>
      </div>
      <div class="mt-3">
        <h4 class="product-header">Nhập coupon</h4>
