import { Injectable } from "@angular/core";
import { HttpClient, HttpHeaders } from "@angular/common/http";
import { Observable } from "rxjs";
import { ResgisterDTO } from "../dtos/user/register.dto";
import { LoginDTO } from "../dtos/user/login.dto";

@Injectable({
  providedIn: "root",
})
export class UserService {
  private apiRegister = "http://localhost:8090/api/v1/users/register";
  private apiLogin = "http://localhost:8090/api/v1/users/login";
  private apiConfig = {
    headers: this
  }
  constructor(private http: HttpClient) {}

  private createHeaders(): HttpHeaders {
    return new HttpHeaders
  }

  register(registerDTO: ResgisterDTO): Observable<any> {
    const headers = new HttpHeaders({
      "Content-Type": "application/json",
    });
    return this.http.post(this.apiRegister, registerDTO, { headers });
  }

  login(loginDTO: LoginDTO): Observable<any> {
    const headers = new HttpHeaders({
      "Content-Type": "application/json",
    });
    return this.http.post(this.apiRegister, loginDTO, { headers });
  }
}
