import { Component, OnInit } from "@angular/core";
import { environment } from "src/app/environments/environment";
import { Product } from "src/app/models/product";
import { CartService } from "src/app/service/cart.service";
import { ProductService } from "src/app/service/product.service";

@Component({
  selector: "app-order-confirm",
  templateUrl: "./order-confirm.component.html",
  styleUrls: ["./order-confirm.component.scss"],
})
export class OrderConfirmComponent implements OnInit {
  cartItems: { product: Product; quantity: number }[] = [];
  totalAmount: number = 0;
  couponCode: string = "";

  constructor(
    private cartService: CartService,
    private productService: ProductService
  ) {}

  ngOnInit(): void {
    //lấy danh sách sản phẩm từ trong giỏ hàng
    debugger;
    const cart = this.cartService.getCart();
    //chuyển  danh sách Id từ Map sang giỏ hàng
    const productIds = Array.from(cart.keys());
    console.log(productIds);
    //gọi service lấy thông tin sản phẩm dựa trên danh sách ID
    debugger;
    this.productService.getProductsByIds(productIds).subscribe({
      next: (products) => {
        debugger;
        //lấy thông tin sp và so luong từ danh sách sp và giỏ hàng
        this.cartItems = productIds.map((productId) => {
          debugger;
          const product = products.find((p) => p.id === productId);
          if (product) {
            product.thumbnail = `${environment.apiBaseUrl}/products/images/${product.thumbnail}`;
          }
          return {
            product: product!,
            quantity: cart.get(productId)!,
          };
        });
        console.log("oke done");
      },
      complete: () => {
        debugger;
        this.calculateTotal();
      },
      error: (error: any) => {
        debugger;
        console.error("Error fetching detail: ", error);
      },
    });
  }

  calculateTotal(): void {
    this.totalAmount = this.cartItems.reduce(
      (total, item) => total + item.product.price * item.quantity,
      0
    );
  }

  applyCoupon(): void {}
}
<app-header></app-header>
<div class="container">
  <div class="confirmation-container">
    <h1>Xác nhân đơn hàng</h1>
    <p>CẢM ƠN BẠN ĐÃ ĐẶT HÀNG. MỜI BẠN XÁC NHẬN LẠI</p>
  </div>
  <div class="row">
    <table>
      <thead>
        <tr>
          <th scope="col" class="text-start">Sản phẩm</th>
          <th scope="col">Số lượng</th>
          <th scope="col">Đơn giá</th>
          <th scope="col">Tổng giá</th>
        </tr>
      </thead>
      <tbody>
        <ng-container *ngFor="let item of cartItems">
          <tr>
            <td>
              <div class="product-info">
                <img
                  [src]="item.product.thumbnail"
                  alt="Product Image"
                  class="product-image"
                />
                <span class="product-name">{{ item.product.name }}</span>
              </div>
            </td>
            <td>{{ item.quantity }}</td>
            <td>
              {{ item.product.price | currency : "VND" : "symbol" : "1.0-0" }}
            </td>
            <td>
              {{
                item.quantity * item.product.price
                  | currency : "VND" : "symbol" : "1.0-0"
              }}
            </td>
          </tr>
        </ng-container>
      </tbody>
    </table>
    <!-- ô nhập mã coupon -->
    <div class="input-group mb-3 mt-3 col-6">
      <input type="text" class="form-control" 
          [(ngModel)]="couponCode" placeholder="Mã giảm giá">
      <div class="input-group-append" style="margin-left: 10px;">
          <button class="btn btn-outline-secondary" type="button" (click)="applyCoupon()">Áp dụng mã</button>
      </div>
  </div>
  <!-- Hiển thị tổng tiền sau khi áp dụng giảm giá -->
  <p class="mb-0">Tổng tiền: {{ totalAmount | currency:'VND':'symbol':'1.0-0' }}</p>
    <div class="text-center mt-3">
      <button class="btn btn-gradient" type="button">Đặt hàng</button>
    </div>
  </div>
</div>
<app-footer></app-footer>
