import { Component, OnInit } from '@angular/core';
import { Product } from 'src/app/dtos/product/product';
import { environtment } from 'src/app/environments/environment';
import { ProductService } from 'src/app/service/product.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  products: Product[] = [];
  currentPage: number = 1;
  itemPerPage: number = 10;
  pages: number[] = [];
  totalPages: number = 0;
  visiblePages: number[] = [];

  constructor(private productService: ProductService) { }

  ngOnInit(): void {
    this.getProducts(this.currentPage, this.itemPerPage);
  }

  getProducts(page: number, limit: number){
    this.productService.getProducts(page, limit).subscribe({
      next: (response: any) => {
        debugger
        response.products.forEach((product: Product) => { 
          //show image
          product.url = `${environtment.apiBaseUrl}/products/images/${product.thumbnail}`;
        });
        this.products = response.products;
        this.totalPages = response.totalPages;
        this.visiblePages = this.generateVisiblePageArray(this.currentPage, this.totalPages);
      },
      complete: () => {debugger},
      error: (error: any) => {
        console.error('Error getting products: ', error);
      }
    });
  }
  //khi bam chuyen phan trang product khac
  onPageChange(page: number) {
    debugger;
    this.currentPage = page;
    this.getProducts(this.currentPage, this.itemPerPage);
  }

  generateVisiblePageArray(currentPage: number, totalPages: number):number[] {
    const maxVisiblePages = 5;
    const halfVisiblePage = Math.floor(maxVisiblePages/2);

    let startPage = Math.max(currentPage - halfVisiblePage, 1);
    let endPage = Math.min(startPage + maxVisiblePages - 1, totalPages);
  
    if(endPage - startPage + 1 < maxVisiblePages) {
      startPage = Math.max(endPage - maxVisiblePages + 1, 1);
    }

    return new Array(endPage - startPage + 1).fill(0)
        .map((_, index) => startPage + index);
  }

}
<app-header></app-header>
<div class="container">
  <div class="intro-section">
    <h1>Đây là trang Home</h1>
    <p>Trang de mua hang</p>
    <p>Xem sp di</p>
  </div>

  <!--Đoạn này có 1 ô searchbox bên trái, 1 combobox ben phai-->
  <div class="search-box">
    <input
      type="text"
      class="form-control search-input"
      placeholder="Tìm sản phẩm"
    />
    <select class="form-control product-category">
      <option selected disabled>Danh mục sản phẩm</option>
      <option>Đồ điện tử</option>
      <option>Hải Sản</option>
      <option>Bánh Kẹo Hàn Quốc</option>
    </select>
  </div>

  <div class="row">
    <div class="col-lg-4 col-md-6" *ngFor="let product of products">
      <div class="product-item">
        <img [src]="product.url" alt="Product Image" class="product-image" />
        <h3 class="product-name">{{ product.name }}</h3>
        <p class="product-description">{{ product.description }}</p>
        <p class="product-price">Giá ${{ product.price }}</p>
      </div>
    </div>
  </div>
</div>

<!--Phân trang-->
<div class="d-flex justify-content-center">
  <nav aria-label="Page navigation">
    <ul class="pagination">
      <li class="page-item" *ngIf="currentPage > 1">
        <a class="page-link" (click)="onPageChange(1)">First</a>
      </li>
      <li class="page-item" *ngIf="currentPage > 1">
        <a class="page-link" href="#" (click)="onPageChange(currentPage - 1)"
          >Previous
        </a>
      </li>
      <ng-container *ngFor="let page of visiblePages">
        <li class="page-item" [ngClass]="{ active: page === currentPage }">
          <a class="page-link" href="#" (click)="onPageChange(page)">{{
            page
          }}</a>
        </li>
      </ng-container>
      <li class="page-item" *ngIf="currentPage < totalPages">
        <a class="page-link" href="#" (click)="onPageChange(currentPage + 1)"
          >Next
        </a>
      </li>
      <li class="page-item" *ngIf="currentPage < totalPages">
        <a class="page-link" href="#" (click)="onPageChange(totalPages)"
          >Last</a
        >
      </li>
    </ul>
  </nav>
</div>

<app-footer></app-footer>
