import { Component, ViewChild } from "@angular/core";
import { NgForm } from "@angular/forms";
import { HttpClient, HttpHeaders } from "@angular/common/http";
import { from } from "rxjs";
import { Router } from "@angular/router";

@Component({
  selector: "app-register",
  templateUrl: "./register.component.html",
  styleUrls: ["./register.component.scss"],
})
export class RegisterComponent {
  @ViewChild("registerForm") registerForm!: NgForm;
  //Khai báo các biến tương ứng trường dữ liệu trên form
  phone: string;
  password: string;
  retypePassword: string;
  fullName: string;
  address: string;
  isAccepted: boolean;
  dateOfBirth: Date;

  constructor(private http: HttpClient, private router: Router) {
    this.phone = "0903925037";
    this.password = "123456";
    this.retypePassword = "123456";
    this.fullName = "Nguyen Ngoc Giau";
    this.address = "76 hvn";
    this.isAccepted = true;
    this.dateOfBirth = new Date();
    this.dateOfBirth.setFullYear(this.dateOfBirth.getFullYear() - 18);
    //inject
  }

  onPhoneChange() {
    console.log(`Phone typed: ${this.phone}`);
  }
  register() {
    const message =
      `phone: ${this.phone}` +
      `password: ${this.password}` +
      `retypePassword: ${this.retypePassword}` +
      `fullName : ${this.fullName} ` +
      `address : ${this.address}` +
      `isAccepted : ${this.isAccepted}` +
      `dateOfBirth: ${this.dateOfBirth}`;
    // alert(message);
    const apiURL = "localhost:8088/api/v1/users/register";
    const registerData = {
      fullname: this.fullName,
      phone_number: this.phone,
      address: this.address,
      password: this.password,
      retype_password: this.retypePassword,
      date_of_birth: this.dateOfBirth,
      facebook_account_id: 0,
      google_account_id: 0,
      role_id: 1,
    };
    const headers = new HttpHeaders({ "Content-Type": "application/json" });
    this.http.post(apiURL, registerData, { headers }).subscribe({
      next: (response: any) => {
        debugger;
        //xử lý kết quả trả về thành cong khi đăng ký
        if (response && (response.status === 200 || response.status === 201)) {
          this.router.navigate(["/login"]);
        } else {
          //xu ly truong hop k dang ky thanh cong
        }
      },
      complete: () => {
        debugger;
      },
      error: (error: any) => {
        //xử lý lỗi nếu có
        debugger;
        console.error("Dang ky khong thanh cong", error);
      },
    });
  }
  checkPasswordsMatch() {
    if (this.password !== this.retypePassword) {
      this.registerForm.form.controls["retypePassword"].setErrors({
        passwordMismatch: true,
      });
    } else {
      this.registerForm.form.controls["retypePassword"].setErrors(null);
    }
  }
  checkAge() {
    if (this.dateOfBirth) {
      const today = new Date();
      const birthDate = new Date(this.dateOfBirth);
      let age = today.getFullYear() - birthDate.getFullYear();
      const monthDiff = today.getMonth() - birthDate.getMonth();

      if (
        monthDiff < 0 ||
        (monthDiff === 0 && today.getDate() < birthDate.getDate())
      ) {
        age--;
      }

      if (age < 18) {
        this.registerForm.form.controls["dateOfBirth"].setErrors({
          invalidAge: true,
        });
      } else {
        this.registerForm.form.controls["dateOfBirth"].setErrors(null);
      }
    }
  }
}
