<div class="container">
  <form #registerForm="ngForm">
    <div class="row">
      <div class="col-md-6 offset-md-3">
        <div class="register-form mx-auto">
          <h2 class="register-header">Đăng ký</h2>
          <div class="form-group">
            <label for="phone">Phone's number</label>
            <input
              type="text"
              class="form-control"
              name="phone"
              [(ngModel)]="phone"
              (input)="onPhoneChange()"
              (focus)="registerForm.form.markAsTouched()"
              required
              id="phone"
            />
            <div class="divider-nospace"></div>
            <p
              class="text-danger"
              *ngIf="phone.length < 6 && registerForm.form.touched"
            >
              Phone phải có ít nhất 6 ký tự
            </p>
          </div>
          <div class="mt-3"></div>
          <div class="form-group password-field">
            <label for="password">Mật khẩu</label>
            <input
              type="password"
              name="password"
              placeholder="Ít nhất 3 ký tự"
              required
              class="form-control"
              [(ngModel)]="password"
              id="password"
            />
            <i class="password-toggle fas fa-eye-slash"></i>
            <div class="divider-nospace"></div>
            <p
              class="text-danger"
              *ngIf="password.length < 3 && registerForm.form.touched"
            >
              Mật khẩu phải có ít nhất 3 ký tự
            </p>
          </div>
          <div class="mt-3"></div>
          <div class="form-group password-field">
            <label for="retype-password">Gõ lại mật khẩu</label>
            <input
              type="password"
              placeholder="Ít nhất 3 ký tự"
              name="retypePassword"
              [(ngModel)]="retypePassword"
              (input)="registerForm.form.markAsTouched(); checkPasswordsMatch()"
              required
              class="form-control"
              id="retype-password"
            />
            <i class="password-toggle fas fa-eye-slash"></i>
            <div class="divider-nospace"></div>
            <p
              class="text-danger"
              *ngIf="retypePassword.length < 3 && registerForm.form.touched"
            >
              Mật khẩu phải có ít nhất 3 ký tự
            </p>
            <p
              class="text-danger"
              *ngIf="
                registerForm.controls['retypePassword'].hasError(
                  'passwordMismatch'
                ) && registerForm.form.touched
              "
            >
              Mật khẩu không khớp
            </p>
          </div>
          <div class="mt-3"></div>
          <div class="form-group">
            <label for="full-name">Họ và tên</label>
            <input
              type="text"
              name="fullName"
              class="form-control"
              required
              id="full-name"
              [(ngModel)]="fullName"
            />
            <div class="divider-nospace"></div>
          </div>
          <div class="form-group">
            <label for="date-of-birth">Ngày tháng năm sinh</label>
            <input
              type="date"
              class="form-control"
              name="dateOfBirth"
              required
              (input)="registerForm.form.markAsTouched(); checkAge()"
              id="date-of-birth"
              [(ngModel)]="dateOfBirth"
            />
            <div class="divider-nospace"></div>
            <p
              class="text-danger"
              *ngIf="
                registerForm.controls['dateOfBirth'].hasError('invalidAge') &&
                registerForm.form.touched
              "
            >
              Bạn chưa đủ 18 tuổi
            </p>
          </div>
          <div class="mt-3"></div>
          <div class="form-group">
            <label for="address">Địa chỉ</label>
            <input
              type="text"
              name="address"
              [(ngModel)]="address"
              class="form-control"
              id="address"
            />
            <div class="divider-nospace"></div>
          </div>
          <div class="form-group">
            <div class="form-check checkbox-text">
              <span style="display: flex">
                <input
                  type="checkbox"
                  name="isAccepted"
                  class="form-check-input"
                  id="agree"
                  [(ngModel)]="isAccepted"
                />
                <label class="form-check-label text-start" for="agree">
                  Tôi đồng ý với các điều khoản và điều kiện
                </label>
              </span>
            </div>
          </div>
          <button type="button" class="register-button" (click)="register()">
            Đăng ký
          </button>
          <div class="divider"></div>
          <p class="text-center">
            Bạn đã có tài khoản?
            <span> <a href="#" class="register-link">Đăng nhập</a></span>
          </p>
        </div>
      </div>
    </div>
  </form>
</div>
<app-footer></app-footer>
