import { Component, ViewChild } from "@angular/core";
import { LoginDTO } from "../dtos/user/login.dto";
import { UserService } from "../service/user.service";
import { Router } from "@angular/router";
import { NgForm } from "@angular/forms";

@Component({
  selector: "app-login",
  templateUrl: "./login.component.html",
  styleUrls: ["./login.component.scss"],
})
export class LoginComponent {
  @ViewChild("loginForm") loginForm!: NgForm;
  phoneNumber: string;
  password: string;

  constructor(private router: Router, private userService: UserService) {
    this.phoneNumber = "0916855344";
    this.password = "string123";
  }

  onPhoneNumberChange() {
    console.log(`Phone typed: ${this.phoneNumber}`);
  }

  login() {
    const message = `phone: ${this.phoneNumber}` + `password: ${this.password}`;
    debugger;

    const loginDTO: LoginDTO = {
      phone_number: this.phoneNumber,
      password: this.password,
    };

    this.userService.login(loginDTO).subscribe({
      next: (response: any) => {
        debugger;
        let token = response.token
        // this.router.navigate(["/login"]);
      },
      complete: () => {
        debugger;
      },
      error: (error: any) => {
        //xử lý lỗi nếu có
        alert(`Cannot login, error: ${error.error}`);
        debugger;
        console.error("Dang nhap khong thanh cong", error);
      },
    });
  }
}
<app-header></app-header>
<div class="container">
    <div class="row">
      <div class="login-form mx-auto">
        <h2 class="login-header">Đăng nhập</h2>
        <div class="form-group">
          <label for="phoneNumber">Phone</label>
          <input
            type="text"
            class="form-control"
            id="phoneNumber"
            [(ngModel)]="phoneNumber"
            (input)="onPhoneNumberChange()"
            (focus)="loginForm.form.markAsTouched()"
          />
          <div class="divider-nospace"></div>
        </div>
        <div class="mt-5"></div>
        <div class="form-group password-field">
          <label for="password">Mật khẩu</label>
          <input
            type="password"
            name="password"
            [(ngModel)]="password"
            placeholder="ít nhất 3 ký tự"
            class="form-control"
            id="password"
          />
          <i class="password-toogle fas fa-eye-slash"></i>
          <div class="divider-nospace"></div>
        </div>
        <div class="form-group">
          <div class="form-check checkbox-text">
            <span style="display: flex">
              <input type="checkbox" class="form-check-input" id="remember" />
              <label class="form-check-label text-start" for="remember"
                >Ghi nhớ đăng nhập</label
              >
            </span>
            <a href="#" class="register-link">Quên mật khẩu</a>
          </div>
        </div>
        <div class="form-group">
          <select class="form-check access-right">
            <option selected disabled>Quyền đăng nhập</option>
            <option>Người dùng</option>
            <option>Quản trị viên</option>
          </select>
        </div>
        <button type="button" class="login-button" (click)="login()">
          Đăng nhập
        </button>
        <div class="divider"></div>
        <p class="text-center">
          Bạn chưa đăng ký?
          <span> <a href="#" class="register-link">Tạo tài khoản</a></span>
        </p>
      </div>
    </div>
</div>
<app-footer></app-footer>
